import wx
class Resourcer():
    def __init__(self, parent):
        self.parent = parent
        
    def getBitMapImg(self, code = "DEFAULT"):
        from cStringIO import StringIO
        img = wx.EmptyImage()
        
        if code == "UI_AUTH":
            str_ui_auth=""
            img.LoadStream(StringIO(str_ui_auth.decode('base64')))
            del str_ui_auth
            return wx.StaticBitmap(self.parent, -1, wx.BitmapFromImage(img))
        elif code == "MSG_WARN":
            msg_warn = ""
            img.LoadStream(StringIO(msg_warn.decode('base64')))
            del  msg_warn
            return wx.StaticBitmap(self.parent, -1, wx.BitmapFromImage(img))            
        elif code == "MSG_OK":
            msg_ok = ""
            img.LoadStream(StringIO(msg_ok.decode('base64'))) 
            del msg_ok
            return wx.StaticBitmap(self.parent, -1, wx.BitmapFromImage(img))
        elif code == "FILE_ICON":
            file_icon = ""
            img.LoadStream(StringIO(file_icon.decode('base64')))
            del file_icon
            return wx.BitmapFromImage(img)
        elif code == "FOLDER_ICON":
            folder_icon = ""
            img.LoadStream(StringIO(folder_icon.decode('base64')))
            del folder_icon
            return wx.BitmapFromImage(img)
        elif code == "FILES_ICON":
            files_icon = ""
            img.LoadStream(StringIO(files_icon.decode('base64')))
            del files_icon
            return wx.BitmapFromImage(img)        
        else:
            default = "iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAAIklEQVR42mNkoDFgHLVg1IJRC0YtGLVg1IJRC0YtGBoWAAB6xAAZ8ZgR7wAAAABJRU5ErkJggg=="
            img.LoadStream(StringIO(default.decode('base64'))) 
            del default
            return wx.StaticBitmap(self.parent, -1, wx.BitmapFromImage(img))
        